const list = document.querySelector('ul'),
  addButton = document.getElementById('addButton'),
  input = document.querySelector('input');
function markDone(e) {
  let t = e.target;
  t.parentNode.classList.toggle('done');
}
function removeItem(e) {
  let t = e.target;
  t.parentNode.remove();
}
function addRemoveButton(e) {
  let t = document.createElement('button');
  (t.className = 'removeButton'),
    (t.innerText = 'Izbri≈°i'),
    t.addEventListener('click', removeItem),
    e.appendChild(t);
}
function addCheckBox(e) {
  let t = document.createElement('input');
  t.setAttribute('type', 'checkbox'),
    t.addEventListener('click', markDone),
    e.insertBefore(t, e.firstChild);
}
function showItemOnScreen() {
  let e = document.createElement('li');
  (e.innerText = input.value),
    list.appendChild(e),
    addRemoveButton(e),
    addCheckBox(e);
}
addButton.addEventListener('click', showItemOnScreen);
